// import React from 'react'
import React, { useEffect, useState } from 'react'
import Search from './Search'
import Filter from './Filter'
import Listing from './Listing'
import HouseMarketReport from './HouseMarketReport'
import HouseRenovationIdeas from './HouseRenovationIdeas'
import PreApproved from './PreApproved'
import Account from './Account'
import NavBar from './NavBar'

function Home() {

  const [list, setList] = useState ([])
  const [user, setUser] = useState ([])
  const [watchedHouse, setWatchedHouse] = useState([])
  const [loading, setLoading] = useState(false);
  //filter
  const [selectedLocation, setSelectedLocation] = useState("All")
  const [selectedPrice, setSelectedPrice] = useState("All")

  useEffect(() => {
    setLoading (true);
    fetch("/list")
    .then(res => res.json())
    .then(data => setList (data))
    .finally(() => setLoading (false))
  }, [])

  function onDelete(dHouse){
    const updatedList = list.filter(house =>house.id !== dHouse.id)
    console.log(updatedList)
    setList(updatedList)
  }

  function onWatch (wHouse){
    if(!watchedHouse.includes(wHouse)){
      setWatchedHouse([...watchedHouse, wHouse])
      console.log(wHouse)
    }
  }

  if (loading) {
    return <p class="fa-duotone fa-spinner">Data is loading...</p>;
  }

  // filter function
  function onLocationChange(location) {
    setSelectedLocation(location)
  }

  function onPriceChange(price) {
    setSelectedPrice(price)
  }

  const listToDisplay = list
  .filter(house => selectedLocation === "All" || house.location === selectedLocation)
  .filter(house => selectedPrice === "All" || house.price === selectedPrice)

  const house = {id: 2, name: 'Mid-top boot - black', price: 75, Location: 'Shoes', image: "https://cdn.shopify.com/s/files/1/0752/4221/products/KPGM027BLK_p_1200x.jpg?v=1512594013", description: "these mid-tops feature a non-slip rubber sole and a synthetic nubuck upper that is both water and oil resistant. The custom insole provides extra heel and arch support,", gender: "Men", condition: "new", causes: [{id: 1, name: 'Vegan', description: 'Fashion house that does not contain any animal mate…ts were used during the entire production process'}, {id: 3, name: 'Made with recycled materials', description: 'Company that produces fashion list made from previously used materials'}, {id: 4, name: 'Made in the USA', description: 'Fashion house manufactured in the USA'}, {id: 5, name: 'Fair labor practices', description: 'Company that pays their employees 20% over the min… and offers a minimum of 14 days of PTOs per year'}, {id: 6, name: 'Charitable donations', description: 'Company that donates part of its profits and/or pr…o charitable causes as part of its business model'}, {id: 7, name: 'Women-owned', description: 'Company that is at least 70% owned by women'}, {id: 8, name: 'Minority-owned', description: 'Company that is at least 70% owned by African-Amer…, Hispanic-Latin American, Native American people'}]}

  const sellerlist = list
  .filter(house => user && house.user_id === user.id)
  .filter(house => selectedLocation === "All" || house.Location === selectedLocation)
  .filter(house => selectedGender === "All" || house.gender === selectedGender)
  .filter(house => isEmpty(selectedCauses) || isEmpty(difference(selectedCauses, house.causes.map(cause => cause.name))))

  console.log(list, "is it define?")
  return (
    <div>
       <NavBar user ={user} setUser ={setUser}/>
        {/* <Search /> */}
        <Account list={list} user={user} onWatch={onWatch} />
        <Filter />
        <Listing list={list} onDelete={onDelete}/>
        <HouseRenovationIdeas />
        <HouseMarketReport />
        <PreApproved />
    </div>
  )
}

export default Home
